using System;
using System.Collections.Generic;
using System.Text;

namespace Muljin.Common.Tests
{
    public class JwtUtilsTests
    {
        private const string tokenValid = @"eyJ0eXAiOiJKV1QiLCJhbGciOiJSUzI1NiIsIng1dCI6Imh1Tjk1SXZQZmVocTM0R3pCRFoxR1hHaXJuTSIsImtpZCI6Imh1Tjk1SXZQZmVocTM0R3pCRFoxR1hHaXJuTSJ9..MgzkWomQSxEn5NkRdpFNG8emxShtg_or04p75RieIh-mCZ_Z-DB1_VHsTd7_iociGIM6Jb0793cSPSKbKZWOMQ2__7qb7RwYg6xZRrCqM5cMYujL8YgKybcJDeWFzwDbPVvuE5GffEKYwD-h6TGbrF1waOc-Qcd_f4nn0Bp4wgXeJ-VEAv-71sgE8zmD5Lws4Y2rueEzkza9nP5HTpq4LnxjWIzYtasArL8uOpNwgbZzdwK4JbwkIbvPqogK5HEcUJ02bt_Zxq28M1FIpdgKsddE1sa8zIitZIsZ6IZspRC34qmylpeyg_sM1i4_UkYLEG_hLdhcM-uNvVEEzxiPNw";
        private const string tokeninvalid = @"eyJ0eXAiOiJKV1QiLCJhbGciOiJSUzI1NiIsIng1dCI6Imh1Tjk1SXZQZmVocTM0R3pCRFoxR1hHaXJuTSIsImtpZCI6Imh1Tjk1SXZQZmVocTM0R3pCRFoxR1hHaXJuTSJ9.eyJhdWQiOiJhcGk6Ly90cnVseWhwY2RldiIsImlzcyI6Imh0dHBzOi";

        [Test]
        public void GivenCorrectTokenFormatValidatesTrue() 
        {

            //arrange
            var res = JwtUtils.ValidateToken(tokenValid);

            //assert
            Assert.That(res, Is.True);
        }

        [Test]
        public void GivenIncorrectTokenFormatValidatesFalse()
        {
            //act and arrange
            var res1 = JwtUtils.ValidateToken(tokeninvalid);
            var res2 = JwtUtils.ValidateToken("");
            var res3 = JwtUtils.ValidateToken(null);


            //assert
            Assert.That(res1, Is.False);
            Assert.That(res2, Is.False);
            Assert.That(res3, Is.False);
        }

        [Test]
        public void GetsSectionBody()
        {
            //act and arrange
            var body = JwtUtils.ExtractTokenBody(tokenValid);

            Assert.That(body, Is.Not.Null);

            Assert.That(body.TryGetProperty("aud", out var aud), Is.True);
            Assert.That(body.TryGetProperty("iss", out var iss), Is.True);
            Assert.That(body.TryGetProperty("iat", out var iat), Is.True);

            Assert.That(aud.GetString(), Is.EqualTo("api://trulyhpcdev"));
            Assert.That(iss.GetString(), Is.EqualTo("https://sts.windows.net/3f23aac6-ee3f-45cb-bd60-fe14f778a467/"));
            Assert.That(iat.TryGetInt32(out var iatInt), Is.True);
            Assert.That(iatInt, Is.EqualTo(1597528771));
        }

    }
}
